import React, { useState, useEffect } from "react";
import { useNavigate, useParams, Link } from "react-router-dom";
import "./AddEdit.css";
import { toast } from "react-toastify";
import axios from "axios";

const initialState = {
  name: "",
  dept_name: "",
  total_cred: "",
};

function AddEdit() {
  const [state, setState] = useState(initialState);
  const { id } = useParams();
  const { name, dept_name, total_cred } = state;
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      axios
        .get(`http://localhost:3001/api/get/${id}`)
        .then((resp) => setState({ ...resp.data[0] }))
        .catch((error) => console.error(error));
    }
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !dept_name || !total_cred) {
      toast.error("Please provide values in each input field");
    } else {
      const requestData = {
        id,
        name,
        dept_name,
        total_cred,
      };

      const apiUrl = id
        ? `http://localhost:3001/api/update/${id}`
        : "http://localhost:3001/api/post";

      const requestMethod = id ? axios.put : axios.post;

      requestMethod(apiUrl, requestData)
        .then(() => {
          setState(initialState);
          toast.success(
            id ? "Student updated successfully" : "Student added successfully"
          );
          setTimeout(() => {
            navigate("/quiz/create");
          }, 500);
        })
        .catch((err) => {
          toast.error(err.response.data);
        });
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setState((prevState) => ({ ...prevState, [name]: value }));
  };

  return (
    <div>
      <div style={{ paddingTop: "100px" }}>
        <form
          style={{
            margin: "auto",
            padding: "15px",
            maxWidth: "400px",
            alignContent: "center",
          }}
          onSubmit={handleSubmit}
        >
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Name"
            value={name || ""}
            onChange={handleInputChange}
          />
          <label htmlFor="dept_name">Department Name</label>
          <input
            type="text"
            id="dept_name"
            name="dept_name"
            placeholder="Department Name"
            value={dept_name || ""}
            onChange={handleInputChange}
          />
          <label htmlFor="total_cred">Total Credits</label>
          <input
            type="number"
            id="total_cred"
            name="total_cred"
            placeholder="Total credits"
            value={total_cred || ""}
            onChange={handleInputChange}
          />
          <div className="button-container">
            <input type="submit" value={id ? "Update" : "Save"} />
            <Link to="/">
              <input type="button" value="Go Back" />
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AddEdit;
