import React, { useState, useEffect } from 'react';
import axios from 'axios';

const QuizForm = () => {
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({
    data_id: '',
    name1: '',
    field1: '',
    field2: '',
  });

  useEffect(() => {
    fetchAllRecords();
  }, []);

  const fetchAllRecords = async () => {
    try {
      const response = await axios.get('/api/datatable');
      setData(response.data);
    } catch (error) {
      console.error('Error fetching records:', error);
    }
  };

   

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  return (
    <div>
      <h1>QuizForm</h1>
      <form>
        <label>
          Data ID:
          <input
            type="text"
            name="data_id"
            value={formData.data_id}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Name:
          <input
            type="text"
            name="name1"
            value={formData.name1}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Field 1:
          <input
            type="text"
            name="field1"
            value={formData.field1}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Field 2:
          <input
            type="text"
            name="field2"
            value={formData.field2}
            onChange={handleChange}
          />
        </label>
        <br />
        <button type="button" onClick={createRecord}>
          Create
        </button>
      </form>
      <h2>Records</h2>
      <table>
        <thead>
          <tr>
            <th>Data ID</th>
            <th>Name</th>
            <th>Field 1</th>
            <th>Field 2</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((record) => (
            <tr key={record.data_id}>
              <td>{record.data_id}</td>
              <td>{record.name1}</td>
              <td>{record.field1}</td>
              <td>{record.field2}</td>
              <td>
                <button type="button" onClick={() => deleteRecord(record.data_id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default QuizForm;
